			SELECT *
			FROM thorarios AS ho
			INNER JOIN sistema_llaves.taulas  	  AS aul  ON aul.numero = ho.num_aula
			INNER JOIN sistema_llaves.tllaves 	  AS llav ON llav.id_aula = aul.id 
